settings.custom_liquid:
{% assign discount_10 = 0.10 %}
{% assign discount_50 = 0.15 %}
{% assign discount_100 = 0.20 %}

<style>
    table {
        width: 100%;
        border-collapse: collapse;
        margin: 20px 0;
        font-size: 1em;
        font-family: 'Arial', sans-serif;
        min-width: 400px;
        box-shadow: 0 0 20px rgba(0, 0, 0, 0.15);
    }
    thead tr {
        background-color: #6EFFDC;
        color: #000000;
        text-align: left;
    }
    th, td {
        padding: 12px 15px;
        border: 1px solid #dddddd;
    }
    tbody tr {
        border-bottom: 1px solid #dddddd;
    }
    tbody tr:nth-of-type(even) {
        background-color: #f3f3f3;
    }
    tbody tr:last-of-type {
        border-bottom: 2px solid #6EFFDC;
    }
    .price-item--regular {

        color: #000000;
    }
</style>

<table>
    <thead>
        <tr>
            <th>Producto</th>
            <th>Precio Unitario (€)</th>
            <th>10 unidades (10% desc) </th>
            <th>50 unidades (15% desc) </th>
            <th>100 unidades (20% desc) </th>
        </tr>
    </thead>
    <tbody>
        <tr>
            <td>{{ product.title }}</td>
            {% if product.compare_at_price %}
                {% assign regular_price = product.compare_at_price | divided_by: 100.0 %}
            {% else %}
                {% assign regular_price = product.price | divided_by: 100.0 %}
            {% endif %}
            <td class="price-item--regular">€{{ regular_price }}</td>
            {% assign discount_amount_10 = regular_price | times: discount_10 %}
            {% assign discount_amount_50 = regular_price | times: discount_50 %}
            {% assign discount_amount_100 = regular_price | times: discount_100 %}
            {% assign price_10 = regular_price | minus: discount_amount_10 %}
            {% assign price_50 = regular_price | minus: discount_amount_50 %}
            {% assign price_100 = regular_price | minus: discount_amount_100 %}
            <td>€{{ price_10 | round: 2 }}</td>
            <td>€{{ price_50 | round: 2 }}</td>
            <td>€{{ price_100 | round: 2 }}</td>
        </tr>
    </tbody>
</table>
